mapper.update:
UPDATE INFORMATION
SET i_Hit = i_Hit+1
WHERE i_No = #{oriNo}

UPDATE INFORMATION
SET i_Good = i_Good+1
WHERE i_No = #{oriNo}

UPDATE INFORMATION
SET i_Good = i_Good-1
WHERE i_No = #{oriNo}

UPDATE INFORMATION
SET i_Title =#{title},
		i_Body  =#{body}
WHERE
		i_No=#{oriNo}

UPDATE INFORMATION
SET    i_IsShow='N'
WHERE  i_No=#{oriNo}
